else {positionRate - positionRate * 0.03}

if (chart.at01hs.base7EMA.ema7 - chart.at01hs.base7EMA.ema7 * 0.2 < 
positionRate - positionRate * 0.03) 
{chart.at01hs.base7EMA.ema7 - chart.at01hs.base7EMA.ema7 * 0.2} 
else {takeProfit}
