/* Definition of the points we are going to use while drawing */
let point1
let point2
let point3
let point4

point1 = {
    x: record.begin,
    y: record.firstMovingAverage - record.firstDeviation
}

point2 = {
    x: record.end,
    y: record.lastMovingAverage - record.lastDeviation
}

point3 = {
    x: record.end,
    y: record.lastMovingAverage + record.lastDeviation
}

point4 = {
    x: record.begin,
    y: record.firstMovingAverage + record.firstDeviation
}

/* Contributing to Auto-Scale*/
coordinateSystem.reportValue(record.firstMovingAverage - record.firstDeviation)
coordinateSystem.reportValue(record.lastMovingAverage - record.lastDeviation)
coordinateSystem.reportValue(record.lastMovingAverage + record.lastDeviation)
coordinateSystem.reportValue(record.firstMovingAverage + record.firstDeviation)

/*
The information we store in files is independent from the charing system and its coordinate systems.
That means that the first thing we allways need to do is to trasform these points to the coordinate system of the timeline.
*/
point1 = coordinateSystem.transformThisPoint(point1)
point2 = coordinateSystem.transformThisPoint(point2)
point3 = coordinateSystem.transformThisPoint(point3)
point4 = coordinateSystem.transformThisPoint(point4)

/*
The browser canvas object does not care about our timeline and its coordinate system. In order to draw on a html canvas we
need the points to be converted into the canvas coordinate system.
Next we transform again to the screen coordinate system.
*/
point1 = transformThisPoint(point1, thisObject.container)
point2 = transformThisPoint(point2, thisObject.container)
point3 = transformThisPoint(point3, thisObject.container)
point4 = transformThisPoint(point4, thisObject.container)

/* We make sure the points do not fall outside the canvas.chartingSpace.viewport visible area. This step allways need to be done.  */
point1 = canvas.chartingSpace.viewport.fitIntoVisibleArea(point1)
point2 = canvas.chartingSpace.viewport.fitIntoVisibleArea(point2)
point3 = canvas.chartingSpace.viewport.fitIntoVisibleArea(point3)
point4 = canvas.chartingSpace.viewport.fitIntoVisibleArea(point4)

point1 = thisObject.fitFunction(point1);
point2 = thisObject.fitFunction(point2);
point3 = thisObject.fitFunction(point3);
point4 = thisObject.fitFunction(point4);

/*
Now that we have our 4 points as coordinates of the actual screen (or html canvas object), we can actaully start drawing.
First we are going to draw the shape backgound.
*/
browserCanvasContext.beginPath()

browserCanvasContext.moveTo(point1.x, point1.y)
browserCanvasContext.lineTo(point2.x, point2.y)
browserCanvasContext.lineTo(point3.x, point3.y)
browserCanvasContext.lineTo(point4.x, point4.y)

browserCanvasContext.closePath()

let opacity = '0.25'  // The backgound will be quite transparent. Possible values goes from 0..1

/* Depending on the direction of the channel, we use a different color for the border lines. */
if (record.direction === 'Side') { browserCanvasContext.strokeStyle = 'rgba(' + UI_COLOR.DARK + ', ' + opacity + ')' }
if (record.direction === 'Up') { browserCanvasContext.strokeStyle = 'rgba(' + UI_COLOR.PATINATED_TURQUOISE + ', ' + opacity + ')' }
if (record.direction === 'Down') { browserCanvasContext.strokeStyle = 'rgba(' + UI_COLOR.RED + ', ' + opacity + ')' }

switch (record.slope) {
    case 'Side': {
        opacity = '0.1';
        break;
    }
    case 'Gentle': {
        opacity = '0.1';
        break;
    }
    case 'Medium': {
        opacity = '0.15';
        break;
    }
    case 'Steep': {
        opacity = '0.2';
        break;
    }
    case 'Extreme': {
        opacity = '0.25';
        break;
    }
}

/* Depending on the direction of the channel, we use a different color for the background. */
if (record.direction === 'Side' || record.slope === 'Side') { browserCanvasContext.fillStyle = 'rgba(' + UI_COLOR.LIGHT + ', ' + opacity + ')'; }
if (record.direction === 'Up') { browserCanvasContext.fillStyle = 'rgba(' + UI_COLOR.GREEN + ', ' + opacity + ')' }
if (record.direction === 'Down') { browserCanvasContext.fillStyle = 'rgba(' + UI_COLOR.RUSTED_RED + ', ' + opacity + ')' }

/* If the mouse is positioned in betwheen the begining and ending of our object, we can opt for changing the color of that specific object. */
if (atMousePosition) {
    browserCanvasContext.fillStyle = 'rgba(' + UI_COLOR.TITANIUM_YELLOW + ', 0.1)'
}

browserCanvasContext.fill() // This is what actually apply the previous instructions on the html canvas object of the browser.

/* Second we are going to draw the shape borders. */
browserCanvasContext.beginPath()

browserCanvasContext.moveTo(point1.x, point1.y)
browserCanvasContext.lineTo(point2.x, point2.y)
browserCanvasContext.lineTo(point3.x, point3.y)
browserCanvasContext.lineTo(point4.x, point4.y)

browserCanvasContext.closePath()

browserCanvasContext.lineWidth = 1          // We use 1 as standard line width
browserCanvasContext.setLineDash([0, 0])    //  We do not want dashed lines in this case.
browserCanvasContext.stroke()               //  This actually draw the borders of our shape.

printLabel('Direction', X_AXIS, PANEL_HEIGHT * 0.05, '1');
