variable.previousSignal = 0

/* Loop Code */

/* 
Calculation method credit: https://www.macroption.com/rsi-calculation/

RSI Calculation Formula

    RSI = 100  100 / ( 1 + RS )
    RS = Relative Strength = AvgU / AvgD
    AvgU = average of all up moves in the last N price bars
    AvgD = average of all down moves in the last N price bars
    N = the period of RSI
    There are 3 different commonly used methods for the exact calculation of AvgU and AvgD (see details below)

RSI Calculation Step by Step

    Calculate up moves and down moves (get U and D)
    Average the up moves and down moves (get AvgU and AvgD)
    Calculate Relative Strength (get RS)
    Calculate the Relative Strength Index (get RSI)
*/

/* Calculating Up Moves and Down Moves */

//let candle = record.current // Our main dependencies are candles 
//let candlePrevious = record.previous

if (record.previous !== undefined) {
    if (record.current.close > record.previous.close) {
        variable.last14U.push(record.current.close - record.previous.close) // Add win to last 14 array.
    } else {
        variable.last14U.push(0) // Add 0 to keep proper count of records in the last 14 array.
    }

    if (record.current.close < record.previous.close) {
        variable.last14D.push(record.previous.close - record.current.close) // Add win to last 14 array.
    } else {
        variable.last14D.push(0) // Add 0 to keep proper count of records in the last 14 array.
    }

    if (variable.last14U.length > 14) {
        variable.last14U.splice(0, 1) // Remove the first element of the array to keep it at a maximun of 14 elements.
    }

    if (variable.last14D.length > 14) {
        variable.last14D.splice(0, 1) // Remove the first element of the array to keep it at a maximun of 14 elements.
    }

    /* Calculating Averages of Up Moves and Down Moves */

    variable.avgU = calculateAVGU(14)
    variable.avgD = calculateAVGD(14)

    /* Calculating Relative Strength */

    if (variable.avgD > 0) {
        relativeStrength = variable.avgU / variable.avgD
    } else {
        relativeStrength = 0
    }

    /* Calculating RSI */

    relativeStrengthIndex = 100 - 100 / ( 1 + relativeStrength)

    if (relativeStrengthIndex !== undefined) {
        variable.value = relativeStrengthIndex.toFixed(2)
    } else {
        variable.value = 0
    }
} else {
    variable.value = 0
}

function calculateAVGU(periods) {  // Having a function avoids us to repeat code.
    /* We check we have enough values to make the calculation */
    if (variable.last14U.length < periods) { return 0 } // If we dont, we define the value is zero.

    let sumU = 0 // Initialize sum variable. 
    for (let j = variable.last14U.length - periods; j < variable.last14U.length; j++) { // Iterate through the last periods
        sumU = sumU + variable.last14U[j]
    }
    let averageU = sumU / periods
    return averageU
}

function calculateAVGD(periods) {  // Having a function avoids us to repeat code.
    /* We check we have enough values to make the calculation */
    if (variable.last14D.length < periods) { return 0 } // If we dont, we define the value is zero.

    let sumD = 0 // Initialize sum variable. 
    for (let i = variable.last14D.length - periods; i < variable.last14D.length; i++) { // Iterate through the last periods
        sumD = sumD + variable.last14D[i]
    }
    let averageD = sumD / periods
    return averageD
}

/* Initialization Code*/

/* 
We keep two separate arrays to store last 14 price increments or losses.
*/

variable.last14U = []
variable.last14D = []

