    function runScript(superAction, templateScript) {
        let target = templateScript.templateTarget.payload.referenceParent
        let templateClone = clone(templateScript.templateStructure.taskManagers)
        target.taskManagers.push(templateClone)
        setup(templateClone, target)
        for (let i = 0; i < templateClone.tasks.length; i++) {
            let task = templateClone.tasks[i]
            if (task.bot !== undefined) {
                if (task.bot.processes !== undefined) {
                    for (let j = 0; j < task.bot.processes.length; j++) {
                        let process = task.bot.processes[j]
                        if (process.marketReference !== undefined) {
                            process.marketReference.payload.referenceParent = market
                        }
                        if (process.session !== undefined) {
                            if (process.session.parameters !== undefined) {
                                if (process.session.parameters.baseAsset !== undefined) {
                                    findExchangeAccountAsset(process.session.parameters.baseAsset, superAction.payload.parentNode.baseAsset)
                                }
                                if (process.session.parameters.quotedAsset !== undefined) {
                                    findExchangeAccountAsset(process.session.parameters.quotedAsset, superAction.payload.parentNode.quotedAsset)
                                }

                                function findExchangeAccountAsset(taskAsset, marketAsset) {
                                    for (let k = 0; k < superAction.payload.parentNode.payload.parentNode.payload.parentNode.exchangeAccounts.userAccounts.length; k++) {
                                        let userAccount = superAction.payload.parentNode.payload.parentNode.payload.parentNode.exchangeAccounts.userAccounts[k]
                                        let found = false
                                        if (userAccount.userAssets !== undefined) {
                                            for (let m = 0; m < userAccount.userAssets.assets.length; m++) {
                                                let asset = userAccount.userAssets.assets[m]
                                                if (asset.payload.referenceParent !== undefined) {
                                                    if (asset.payload.referenceParent.id === marketAsset.payload.referenceParent.id) {
                                                        taskAsset.payload.referenceParent = asset
                                                        found = true
                                                        break
                                                    }
                                                }
                                            }
                                        }
                                        if (found === true) { break }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        return templateClone
    }
}

templateScriptConstructor = function () {
    let thisObject = {
        runValidations: runValidations,
